sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */
	data.isPublisher=false;

	var gr1=new GlideRecord('x_ggisu_esg_sustan_esgs_fashion_publishers_collection');
	gr1.addEncodedQuery('publisher_name=javascript:gs.getUserID();');
	gr1.query();
	if(gr1.next()){
		data.isPublisher=true;
	}


	if(input && input.value){
  var gr=new GlideRecord('x_ggisu_esg_sustan_esgs_fashion_publishers_collection');
	gr.addEncodedQuery('publisher_name!=javascript:gs.getUserID();');
	gr.query();
	if(gr.next()){
		gr.initialize();
		gr.publisher_name=gs.getUserID();
		gr.publisher_cheer_points='0';
		gr.insert();
		gs.addInfoMessage('You are now a Seller,Start selling your products !!!');
		data.isPublisher=true;
	}
}


})();
