sp_widget.client_script:
api.controller=function($uibModal,$scope) {
  /* widget controller */
  var c = this;

	c.showImages=function(sysId){
			c.modalInstance = $uibModal.open({
                            templateUrl: 'open_images',
                            scope: $scope,
			                       backdrop: 'static'
                        });
		   c.data.sysId=sysId;
		   c.server.update();

	}

	c.close=function(){
			c.modalInstance.close();
   }

};

